import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import L from 'leaflet';
import 'leaflet-routing-machine';
import { signOut, auth } from '../firebase';
import { useNavigate } from 'react-router-dom';

function Routing({ points, setInfo }) {
  const map = useMap();

  useEffect(() => {
    if (points.length === 2) {
      const control = L.Routing.control({
        waypoints: points,
        routeWhileDragging: false,
        show: false,
      }).addTo(map);

      control.on('routesfound', function (e) {
        const route = e.routes[0];
        const distance = (route.summary.totalDistance / 1000).toFixed(2);
        const time = (route.summary.totalTime / 60).toFixed(2);
        setInfo(`ðŸ›£ï¸ Distance: ${distance} km | ðŸ•’ Time: ${time} min`);
      });

      return () => map.removeControl(control);
    }
  }, [points, map, setInfo]);

  return null;
}

function MapPage() {
  const [points, setPoints] = useState([]);
  const [info, setInfo] = useState('');
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth).then(() => navigate('/'));
  };

  const handleReset = () => {
    setPoints([]);
    setInfo('');
  };

  return (
    <div>
      <div style={{ padding: '10px', textAlign: 'center' }}>
        <button onClick={handleLogout} style={{ marginRight: '10px' }}>Logout</button>
        <button onClick={handleReset}>Reset</button>
        <div style={{ marginTop: '10px', fontSize: '16px', fontWeight: 'bold' }}>{info}</div>
      </div>

      <MapContainer
        center={[12.9716, 77.5946]}
        zoom={13}
        style={{ height: '90vh', width: '100%' }}
        whenCreated={(map) => {
          map.on('click', (e) => {
            if (points.length >= 2) return;

            const newPoint = L.latLng(e.latlng.lat, e.latlng.lng);
            setPoints((prev) => [...prev, newPoint]);
            setInfo('');
            console.log("Point added:", newPoint); // ðŸ§ª Debug log
          });
        }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {points.map((pos, idx) => (
          <Marker key={idx} position={pos}></Marker>
        ))}
        {points.length === 2 && <Routing points={points} setInfo={setInfo} />}
      </MapContainer>
    </div>
  );
}

export default MapPage;
