import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer, Marker, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-routing-machine';
import { signOut, auth } from '../firebase';
import { useNavigate } from 'react-router-dom';

function Routing({ points, setInfo }) {
  const map = useMap();

  useEffect(() => {
    if (points.length === 2) {
      const routeControl = L.Routing.control({
        waypoints: points,
        routeWhileDragging: false,
        show: false,
      }).addTo(map);

      routeControl.on('routesfound', function (e) {
        const route = e.routes[0];
        const distance = (route.summary.totalDistance / 1000).toFixed(2);
        const duration = (route.summary.totalTime / 60).toFixed(2);
        setInfo(`🛣️ Distance: ${distance} km | 🕒 Time: ${duration} mins`);
      });

      return () => map.removeControl(routeControl);
    }
  }, [points, map, setInfo]);

  return null;
}

function MapPage() {
  const [points, setPoints] = useState([]);
  const [info, setInfo] = useState('');
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth).then(() => navigate('/'));
  };

  const handleMapClick = (e) => {
    if (points.length >= 2) return;
    const newPoint = L.latLng(e.latlng.lat, e.latlng.lng);
    setPoints([...points, newPoint]);
    setInfo('');
  };

  const reset = () => {
    setPoints([]);
    setInfo('');
  };

  return (
    <div>
      <div style={{ textAlign: 'center', padding: '10px' }}>
        <button onClick={handleLogout} style={{ marginRight: '10px' }}>Logout</button>
        <button onClick={reset} style={{ marginRight: '10px' }}>Reset</button>
        <div style={{ fontWeight: 'bold', marginTop: '8px' }}>{info}</div>
      </div>

      <MapContainer
        center={[12.9716, 77.5946]}
        zoom={13}
        style={{ height: '90vh', width: '100%' }}
        whenCreated={(map) => map.on('click', handleMapClick)}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {points.map((pos, idx) => (
          <Marker key={idx} position={pos}></Marker>
        ))}
        {points.length === 2 && <Routing points={points} setInfo={setInfo} />}
      </MapContainer>
    </div>
  );
}

export default MapPage;
