import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import { auth, signOut } from '../firebase';
import { useNavigate } from 'react-router-dom';

// Custom Routing component to handle drawing
function Routing({ waypoints, setInfo }) {
  const map = useMap();

  useEffect(() => {
    if (waypoints.length < 2) return;

    const routingControl = L.Routing.control({
      waypoints: waypoints,
      routeWhileDragging: false,
      show: false,
      addWaypoints: false,
      lineOptions: {
        styles: [{ color: 'blue', weight: 4 }],
      },
    }).addTo(map);

    routingControl.on('routesfound', function (e) {
      const route = e.routes[0];
      const distance = (route.summary.totalDistance / 1000).toFixed(2);
      const duration = (route.summary.totalTime / 60).toFixed(2);
      setInfo(`🛣️ Distance: ${distance} km | 🕒 Time: ${duration} mins`);
    });

    return () => map.removeControl(routingControl);
  }, [waypoints, map, setInfo]);

  return null;
}

function MapPage() {
  const [points, setPoints] = useState([]);
  const [info, setInfo] = useState('');
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth).then(() => navigate('/'));
  };

  const handleReset = () => {
    setPoints([]);
    setInfo('');
  };

  const handleMapClick = (e) => {
    if (points.length >= 2) return;

    const clickedPoint = L.latLng(e.latlng.lat, e.latlng.lng);
    setPoints([...points, clickedPoint]);
    console.log('Point added:', clickedPoint);
  };

  return (
    <div>
      <div style={{ padding: '10px', textAlign: 'center' }}>
        <button onClick={handleLogout} style={{ marginRight: '10px' }}>
          Logout
        </button>
        <button onClick={handleReset}>Reset</button>
        <div style={{ marginTop: '10px', fontSize: '16px', fontWeight: 'bold' }}>{info}</div>
      </div>

      <MapContainer
        center={[12.9716, 77.5946]} // Bengaluru center
        zoom={13}
        style={{ height: '90vh', width: '100%' }}
        whenCreated={(map) => map.on('click', handleMapClick)}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {points.map((pos, index) => (
          <Marker key={index} position={pos} />
        ))}
        {points.length === 2 && <Routing waypoints={points} setInfo={setInfo} />}
      </MapContainer>
    </div>
  );
}

export default MapPage;
